<?php require __DIR__.'/config.php'; require __DIR__.'/db.php'; $db=db(); $type=$_GET['type']??''; $id=(int)($_GET['id']??0); $items=($type==='resort')?$db->query("SELECT * FROM resorts ORDER BY name")->fetchAll(PDO::FETCH_ASSOC):$db->query("SELECT * FROM activities ORDER BY name")->fetchAll(PDO::FETCH_ASSOC); if(!in_array($type,['activity','resort'])) $type='activity'; if($id===0 && !empty($items)) $id=$items[0]['id']; if(session_status()!==PHP_SESSION_ACTIVE) session_start(); $csrf=csrf_token(); ?>
<!doctype html><html lang='en'><head><meta charset='utf-8'><title>Book | <?=clean($SITE_NAME)?></title><meta name='viewport' content='width=device-width, initial-scale=1'><link href='https://cdn.jsdelivr.net/npm/bootstrap@5.3.3/dist/css/bootstrap.min.css' rel='stylesheet'><link rel='stylesheet' href='assets/style.css'></head><body><div class='container py-5'><h1 class='mb-4'>Quick Booking</h1><form method='post' action='booking_submit.php' class='row g-3'><input type='hidden' name='csrf' value='<?=$csrf?>'><div class='col-md-4'><label class='form-label'>Booking For</label><select name='type' class='form-select' onchange="location.href='booking.php?type='+this.value"><option value='activity' <?=$type==='activity'?'selected':''?>>Activity</option><option value='resort' <?=$type==='resort'?'selected':''?>>Resort/Stay</option></select></div><div class='col-md-8'><label class='form-label'><?=$type==='resort'?'Select Resort':'Select Activity'?></label><select name='ref_id' class='form-select'><?php foreach($items as $it):?><option value='<?=$it['id']?>' <?=$id===$it['id']?'selected':''?>><?=clean($it['name'])?></option><?php endforeach;?></select></div><?php if($type==='resort'):?><div class='col-md-3'><label class='form-label'>Check‑in</label><input type='date' name='checkin' class='form-control' required></div><div class='col-md-3'><label class='form-label'>Check‑out</label><input type='date' name='checkout' class='form-control' required></div><?php else:?><div class='col-md-3'><label class='form-label'>Date</label><input type='date' name='activity_date' class='form-control' required></div><?php endif;?><div class='col-md-3'><label class='form-label'>No. of People</label><input type='number' name='people' min='1' max='50' class='form-control' required></div><div class='col-md-4'><label class='form-label'>Your Name</label><input type='text' name='name' class='form-control' required></div><div class='col-md-4'><label class='form-label'>Email</label><input type='email' name='email' class='form-control' required></div><div class='col-md-4'><label class='form-label'>Phone</label><input type='tel' name='phone' pattern='[0-9+\-\s]{8,15}' class='form-control' required></div><div class='col-12'><label class='form-label'>Notes (optional)</label><textarea name='notes' class='form-control' rows='3' placeholder='Any preferences, kid ages, etc.'></textarea></div><div style='position:absolute; left:-9999px;'><label>Leave blank</label><input type='text' name='website'></div><div class='col-12'><button class='btn btn-brand btn-lg'>Submit Booking</button> <a href='index.php' class='btn btn-outline-secondary'>Back</a></div></form></div></body></html>